import { Component, HostListener, Inject, OnInit } from '@angular/core';
import { ActivatedRoute, NavigationEnd, ParamMap, Router } from '@angular/router';
import { take, filter } from "rxjs/operators"
import { Character } from '@app/shared/interfaces/character.interface';
import { CharacterService } from '@app/shared/services/character.service';
import { DOCUMENT } from '@angular/common';
type RequestInfo = {
  next: string;
}

@Component({
  selector: 'app-character-list',
  templateUrl: './character-list.component.html',
  styleUrls: ['./character-list.component.scss']
})
export class CharacterListComponent implements OnInit{
  characters: Character[] = [];
  info: RequestInfo = {
    next: '',
  };
  showGoUpButton = false;
  private pageNum = 1;
  private query: string = '';
  private hideScrollHeight = 200;
  private showScrollHeight = 500;

  constructor(
    @Inject(DOCUMENT) private document: Document,
    private characterSvc: CharacterService, 
    private route: ActivatedRoute, 
    private router:Router
  ){
    this.onUrlChange();
  }

  ngOnInit(): void {
    // this.getDataFromService();
    this.getCharactersByQuery();
  }

  private onUrlChange(): void {
    this.router.events
      .pipe(filter((event) => event instanceof NavigationEnd))
      .subscribe(() => {
        this.characters = [];
        this.pageNum = 1;
        this.getCharactersByQuery();
      });
  }

  private getCharactersByQuery(): void {
    this.route.queryParams
      .pipe(take(1))
      .subscribe((params:any) => {
        this.query = params['q'];
        this.getDataFromService();
      })
  }

  private getDataFromService(): void {
      this.characterSvc
        .searchCharacters(this.query, this.pageNum)
        .pipe(take(1))
        .subscribe((res:any) => {
        if(res?.results?.length){
          const { info, results} = res
          this.characters = [...this.characters, ...results];
          this.info = info; 
        } else {
          this.characters = [];
        }

      })
  }

  @HostListener('window:scroll', [])
  onWindowScroll():void {
    const yOffSet = window.pageYOffset;
    if((yOffSet || this.document.documentElement.scrollTop || this.document.body.scrollTop) > this.showScrollHeight) {
      this.showGoUpButton = true;
    } else if (this.showGoUpButton && (yOffSet || this.document.body.scrollTop || this.document.documentElement.scrollTop) < this.hideScrollHeight) {
      this.showGoUpButton = false
    }
  }

  private onScrollDown(): void{
  if(this.info.next){
      this.pageNum++;
      this.getDataFromService();
    }
  }

  private onScrollTop():void {
    this.document.body.scrollTop = 0;
    this.document.documentElement.scrollTop = 0;
  }
}
<div class="row" infinite-scroll(scrolled)="onScrollDown()">
  <div class="col-xs-12 col-sm-6 col-md-4" *ngFor="let character of characters">
    <app-character [character]="character"></app-character>
  </div>
  <div
    class="col-xs-12 col-sm-12 col-md-12 mt-5 container-notFound"
    *ngIf="characters.length == 0"
  >
    <p>No results...</p>
  </div>
</div>
<button
  [ngClass]="'no-hidden'"
  *ngIf="showGoUpButton"
  class="btn btn-dark"
  (click)="onScrollTop()"
>
  Go Up
</button>
