import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CharacterService } from '@app/shared/services/character.service';
import { Observable } from 'rxjs';
import { Location } from '@angular/common';
import { Character } from '@app/shared/interfaces/character.interface';

@Component({
  selector: 'app-character-details',
  templateUrl: './character-details.component.html',
  styleUrls: ['./character-details.component.scss']
})
export class CharacterDetailsComponent implements OnInit {
  character$: Observable<Character>;

  constructor(
    private route: ActivatedRoute, 
    private characterSvc:CharacterService, 
    private location:Location){}

  ngOnInit(): void {
    this.route.params.pipe(take(1)).subscribe((params) => {
      const id = params['id'];
      this.character$ = this.characterSvc.getDeatils(id);
    })
  }

  onGoBack(): void {
    this.location.back();
  }
}
<div class="row">
  <div class="col-xs-12 col-sm-6 col-md-4">
    <div class="card">
      <div class="image">
        <img
          [src]="character.image"
          [alt]="character.name"
          class="card-img-top"
        />
      </div>
      <div class="card-inner">
        <div class="header">
          <h2>{{ character.name | slice : 0 : 15 }}</h2>
          <h4 class="text-muted">{{ character.gender }}</h4>
          <small class="textmuted">{{ character.created | date }}</small>
        </div>
      </div>
    </div>
  </div>
</div>
<button class="btn btn-dark">Go Back</button>
