import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, NavigationEnd, ParamMap, Router } from '@angular/router';
import { take, filter } from "rxjs/operators"
import { Character } from '@app/shared/interfaces/character.interface';
import { CharacterService } from '@app/shared/services/character.service';
type RequestInfo = {
  next: string;
}

@Component({
  selector: 'app-character-list',
  templateUrl: './character-list.component.html',
  styleUrls: ['./character-list.component.scss']
})
export class CharacterListComponent implements OnInit{
  characters: Character[] = [];
  info: RequestInfo = {
    next: '',
  };
  private pageNum = 1;
  private query: string = '';
  private hideScrollHeight = 200;
  private showScrollHeight = 500;

  constructor(private characterSvc: CharacterService, private route: ActivatedRoute, private router:Router){
    this.onUrlChange();
  }

  ngOnInit(): void {
    // this.getDataFromService();
    this.getCharactersByQuery();
  }

private onUrlChange(): void {
  this.router.events
    .pipe(filter((event) => event instanceof NavigationEnd))
    .subscribe(() => {
      this.characters = [];
      this.pageNum = 1;
      this.getCharactersByQuery();
    });
}

  private getCharactersByQuery(): void {
    this.route.queryParams
      .pipe(take(1))
      .subscribe((params:any) => {
        this.query = params['q'];
        this.getDataFromService();
      })
  }

  private getDataFromService(): void {
    console.log('query', this.query)
      this.characterSvc
        .searchCharacters(this.query, this.pageNum)
        .pipe(take(1))
        .subscribe((res:any) => {
        if(res?.results?.length){
          const { info, results} = res
          this.characters = [...this.characters, ...results];
          this.info = info; 
        } else {
          this.characters = [];
        }

      })
  }

}
<div class="row">
  <div class="col-xs-12 col-sm-6 col-md-4" *ngFor="let character of characters">
    <div class="card">
      <div class="image">
        <a [routerLink]="['/character-details', character.id]">
          <img
            [src]="character.image"
            [alt]="character.name"
            class="card-img-top"
          />
        </a>
      </div>
      <div class="card-inner">
        <div class="header">
          <h2>{{ character.name }}</h2>
          <h4 class="text-muted">{{ character.gender }}</h4>
          <small class="textmuted">{{ character.created }}</small>
        </div>
      </div>
    </div>
  </div>
  <div
    class="col-xs-12 col-sm-12 col-md-12 mt-5 container-notFound"
    *ngIf="characters.length == 0"
  >
    <p>No results...</p>
  </div>
</div>
<button class="btn btn-dark">Go Up</button>
